package vip2012.g07.shoppinglist;import android.content.ContentValues;import android.content.Context;import android.database.SQLException;import android.database.sqlite.SQLiteDatabase;public class ItemsDataSource {	private SQLiteDatabase database;	private ItemHelper dbHelper;	private String[] allColumns = { ItemHelper.ID,			ItemHelper.NAME,ItemHelper.PLACE, ItemHelper.PRICE, ItemHelper.PRIORITY, ItemHelper.QUANTITY,ItemHelper.STATUS,ItemHelper.TIME, ItemHelper.UNIT };	public ItemsDataSource(Context context) {		dbHelper = new ItemHelper(context);			}	public void open() throws SQLException {		database = dbHelper.getWritableDatabase();		database.delete("item", null, null);	}	public void close() {		dbHelper.close();	}	public void emptyTable(){	}		public void createItem(String name, String priority, 			String quantity, String price, 			String unit, String status, String place, String time) {		ContentValues values = new ContentValues();		values.put(ItemHelper.NAME, name);		values.put(ItemHelper.PLACE, place );		values.put(ItemHelper.PRICE, price );		values.put(ItemHelper.PRIORITY, priority );		values.put(ItemHelper.STATUS, status);		values.put(ItemHelper.TIME, time );		values.put(ItemHelper.UNIT, unit );		Integer insertId = (int) database.insert(ItemHelper.ITEM_TABLE_NAME, null,				values);	}					}